mapper.select:
(SELECT a.id, a.content, ifnull( a.thumbs_up, 0 ) thumbs_up, ifnull( a.share, 0 ) share, ifnull( a.collection_num, 0 ) collection_num, a.type, ifnull(b.id,'') thumbs_id, ifnull(c.id,'') collection_id FROM tl_kb_joke a LEFT JOIN ( SELECT * FROM tl_kb_joke_thumbs WHERE user_id = #{openid}) b ON a.id = b.joke_id LEFT JOIN ( SELECT * FROM tl_user_sub_joke WHERE user_id = #{openid}) c ON a.id = c.joke_id WHERE a.id = #{searchKey}) UNION ALL(
SELECT a.id, a.content, ifnull( a.thumbs_up, 0 ) thumbs_up, ifnull( a.share, 0 ) share, ifnull( a.collection_num, 0 ) collection_num, a.type, ifnull(b.id,'') thumbs_id, ifnull(c.id,'') collection_id FROM tl_kb_joke a LEFT JOIN ( SELECT * FROM tl_kb_joke_thumbs WHERE user_id = #{openid}) b ON a.id = b.joke_id LEFT JOIN ( SELECT * FROM tl_user_sub_joke WHERE user_id = #{openid}) c ON a.id = c.joke_id
WHERE a.id != #{searchKey}
ORDER BY -- ifnull( thumbs_up, 0 ) DESC rand()
)
