mapper.select:
(SELECT a.id, a.title, a.content, a.answer, ifnull( a.thumbs_num, 0 ) thumbs_num, ifnull( a.share_num, 0 ) share_num, ifnull( a.collection_num, 0 ) collection_num, a.qa_type, ifnull(b.id,'') thumbs_id, ifnull(c.id,'') collection_id FROM tl_kb_qa a LEFT JOIN ( SELECT * FROM tl_kb_qa_thumbs WHERE user_id = #{openid}) b ON a.id = b.qa_id LEFT JOIN ( SELECT * FROM tl_user_sub_qa WHERE user_id = #{openid}) c ON a.id = c.qa_id WHERE a.id = #{shareId}) UNION ALL(
SELECT a.id, a.title, a.content, a.answer, ifnull( a.thumbs_num, 0 ) thumbs_num, ifnull( a.share_num, 0 ) share_num, ifnull( a.collection_num, 0 ) collection_num, a.qa_type, ifnull(b.id,'') thumbs_id, ifnull(c.id,'') collection_id FROM tl_kb_qa a LEFT JOIN ( SELECT * FROM tl_kb_qa_thumbs WHERE user_id = #{openid}) b ON a.id = b.qa_id LEFT JOIN ( SELECT * FROM tl_user_sub_qa WHERE user_id = #{openid}) c ON a.id = c.qa_id WHERE 1=1
AND a.id != #{shareId}
AND qa_type=#{type}
AND (a.content like '%${searchKey}%' or a.answer like '%${searchKey}%')
ORDER BY rand()
)
